import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/notionClient.ts.content:
import { Client } from '@notionhq/client';

let connectionSettings: any;

async function getAccessToken() {
  if (connectionSettings && connectionSettings.settings.expires_at && new Date(connectionSettings.settings.expires_at).getTime() > Date.now()) {
    return connectionSettings.settings.access_token;
  }

  const hostname = process.env.REPLIT_CONNECTORS_HOSTNAME
  const xReplitToken = process.env.REPL_IDENTITY 
    ? 'repl ' + process.env.REPL_IDENTITY 
    : process.env.WEB_REPL_RENEWAL 
    ? 'depl ' + process.env.WEB_REPL_RENEWAL 
    : null;

  if (!xReplitToken) {
    throw new Error('X_REPLIT_TOKEN not found for repl/depl');
  }

  connectionSettings = await fetch(
    'https://' + hostname + '/api/v2/connection?include_secrets=true&connector_names=notion',
    {
      headers: {
        'Accept': 'application/json',
        'X_REPLIT_TOKEN': xReplitToken
      }
    }
  ).then(res => res.json()).then(data => data.items?.[0]);

  const accessToken = connectionSettings?.settings?.access_token || connectionSettings.settings?.oauth?.credentials?.access_token;

  if (!connectionSettings || !accessToken) {
    throw new Error('Notion not connected');
  }
  return accessToken;
}

// WARNING: Never cache this client.
// Access tokens expire, so a new client must be created each time.
// Always call this function again to get a fresh client.
export async function getUncachableNotionClient() {
  const accessToken = await getAccessToken();
  return new Client({ auth: accessToken });
}

server/openai.ts.content:
import { GoogleGenAI } from "@google/genai";

// Using Gemini AI for workspace generation with free API key
const genAI = new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY || "" });

export interface NotionWorkspaceSpec {
  title: string;
  description: string;
  databases: NotionDatabase[];
  pages: NotionPage[];
  theme: string;
  layout: string;
}

export interface NotionDatabase {
  name: string;
  description: string;
  properties: NotionProperty[];
  views: NotionView[];
  relations: NotionRelation[];
  sampleData: any[];
}

export interface NotionProperty {
  name: string;
  type: string;
  config?: any;
}

export interface NotionView {
  name: string;
  type: string;
  filters?: any[];
  sorts?: any[];
  layout?: string;
}

export interface NotionRelation {
  property: string;
  relatedDatabase: string;
  type: string;
}

export interface NotionPage {
  title: string;
  content: string;
  type: string;
}

export async function generateNotionWorkspace(
  prompt: string,
  theme: string = "professional"
): Promise<NotionWorkspaceSpec> {
  try {
    const systemPrompt = `You are an expert Notion workspace designer. Generate a complete, production-ready Notion workspace specification based on the user's requirements.

IMPORTANT GUIDELINES:
- Create sophisticated, enterprise-grade workspaces with advanced features
- Include realistic property types: title, rich_text, select, multi_select, date, checkbox, number, formula, rollup, relation, people, status
- Generate meaningful formulas and rollups that add business value
- Create multiple view types: table, board, calendar, gallery, list, timeline
- Establish proper database relationships (one-to-one, one-to-many, many-to-many)
- Include 5-10 realistic sample data entries per database
- Apply the specified theme: ${theme}
- Ensure all sample data is realistic and contextual (no lorem ipsum)

THEMES:
- professional: Clean, corporate colors, formal structure
- pastel: Soft, muted colors, gentle aesthetics
- dark: Dark backgrounds, high contrast
- fun: Bright colors, playful elements
- light: Bright, minimal, clean
- cheerful: Warm, inviting colors
- loving: Soft pinks and warm tones
- soft: Gentle, rounded aesthetics
- rough: Bold, industrial design

Respond with valid JSON matching the NotionWorkspaceSpec interface.`;

    const response = await genAI.models.generateContent({
      model: "gemini-2.5-flash",
      config: {
        systemInstruction: systemPrompt,
        responseMimeType: "application/json",
      },
      contents: prompt,
    });

    const result = JSON.parse(response.text || "{}");
    return result as NotionWorkspaceSpec;
  } catch (error) {
    console.error("Error generating workspace:", error);
    throw new Error("Failed to generate Notion workspace specification");
  }
}

export async function refineWorkspaceSpec(
  currentSpec: NotionWorkspaceSpec,
  refinementPrompt: string
): Promise<NotionWorkspaceSpec> {
  try {
    const systemPrompt = `You are refining an existing Notion workspace specification. Make the requested changes while maintaining the overall structure and quality.

Current workspace specification:
${JSON.stringify(currentSpec, null, 2)}

Apply the following refinements and return the updated specification as valid JSON:`;

    const response = await genAI.models.generateContent({
      model: "gemini-2.5-flash",
      config: {
        systemInstruction: systemPrompt,
        responseMimeType: "application/json",
      },
      contents: refinementPrompt,
    });

    const result = JSON.parse(response.text || "{}");
    return result as NotionWorkspaceSpec;
  } catch (error) {
    console.error("Error refining workspace:", error);
    throw new Error("Failed to refine workspace specification");
  }
}

export async function generateChatResponse(
  messages: any[],
  context?: any
): Promise<string> {
  try {
    const systemPrompt = `You are an AI assistant for Nicer SaaS, a platform that creates Notion workspaces from natural language.

Your role:
- Help users describe their workspace requirements clearly
- Suggest improvements and additional features
- Explain Notion concepts and capabilities
- Guide users through the workspace creation process
- Be encouraging and helpful

Keep responses concise but informative. Focus on understanding the user's needs and helping them create the best possible workspace.`;

    const response = await genAI.models.generateContent({
      model: "gemini-2.5-flash",
      config: {
        systemInstruction: systemPrompt,
      },
      contents: messages.map(msg => `${msg.role}: ${msg.content}`).join('\n'),
    });

    return response.text || "";
  } catch (error) {
    console.error("Error generating chat response:", error);
    throw new Error("Failed to generate chat response");
  }
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import Stripe from "stripe";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { getUncachableNotionClient } from "./notionClient";
import { generateNotionWorkspace, refineWorkspaceSpec, generateChatResponse } from "./openai";
import { insertWorkspaceSchema, insertConversationSchema } from "@shared/schema";
import { z } from "zod";

// Initialize Stripe
if (!process.env.STRIPE_SECRET_KEY) {
  console.warn('STRIPE_SECRET_KEY not provided - payment features will be disabled');
}

const stripe = process.env.STRIPE_SECRET_KEY ? new Stripe(process.env.STRIPE_SECRET_KEY, {
  apiVersion: "2025-08-27.basil",
}) : null;

// Get Notion user information
async function getNotionUserInfo(notion: any) {
  try {
    const user = await notion.users.me();
    return {
      email: user.person?.email || user.bot?.owner?.user?.person?.email || "Unknown",
      name: user.name || "Unknown User",
      id: user.id
    };
  } catch (error) {
    console.error("Error getting Notion user info:", error);
    return { email: "Unknown", name: "Unknown User", id: "unknown" };
  }
}

// Notion workspace deployment function
async function deployWorkspaceToNotion(notion: any, workspaceData: any, userInfo?: any): Promise<{ url: string; notionUser: any }> {
  try {
    // Create a main workspace page
    const parentPage = await notion.pages.create({
      parent: {
        type: "page_id",
        page_id: process.env.NOTION_PAGE_ID || await getUserNotionPageId(notion)
      },
      properties: {
        title: {
          title: [
            {
              text: {
                content: workspaceData.title || "Generated Workspace"
              }
            }
          ]
        }
      },
      children: [
        {
          object: "block",
          type: "heading_1",
          heading_1: {
            rich_text: [
              {
                type: "text",
                text: {
                  content: workspaceData.title || "Generated Workspace"
                }
              }
            ]
          }
        },
        {
          object: "block",
          type: "paragraph",
          paragraph: {
            rich_text: [
              {
                type: "text",
                text: {
                  content: workspaceData.description || "AI-generated workspace created with Nicer SaaS"
                }
              }
            ]
          }
        }
      ]
    });

    // Create databases from the workspace data
    if (workspaceData.databases && Array.isArray(workspaceData.databases)) {
      // Create databases sequentially to avoid Notion API conflicts
      for (const db of workspaceData.databases) {
        try {
          // Transform properties to Notion format
          const notionProperties: any = {};

          if (db.properties && Array.isArray(db.properties)) {
            db.properties.forEach((prop: any) => {
              const propName = prop.name || prop.title || "Untitled";
              switch (prop.type) {
                case "text":
                case "title":
                  notionProperties[propName] = { title: {} };
                  break;
                case "number":
                  notionProperties[propName] = { number: { format: "number" } };
                  break;
                case "select":
                  notionProperties[propName] = { 
                    select: { 
                      options: (prop.options || []).map((opt: any) => ({
                        name: opt.name || opt,
                        color: opt.color || "default"
                      }))
                    }
                  };
                  break;
                case "multiselect":
                case "multi_select":
                  notionProperties[propName] = { 
                    multi_select: { 
                      options: (prop.options || []).map((opt: any) => ({
                        name: opt.name || opt,
                        color: opt.color || "default"
                      }))
                    }
                  };
                  break;
                case "date":
                  notionProperties[propName] = { date: {} };
                  break;
                case "checkbox":
                  notionProperties[propName] = { checkbox: {} };
                  break;
                case "url":
                  notionProperties[propName] = { url: {} };
                  break;
                case "email":
                  notionProperties[propName] = { email: {} };
                  break;
                case "phone":
                  notionProperties[propName] = { phone_number: {} };
                  break;
                case "formula":
                  notionProperties[propName] = { 
                    formula: { expression: prop.formula || "1" }
                  };
                  break;
                case "relation":
                  // Skip relations for now as they require target database
                  break;
                default:
                  notionProperties[propName] = { rich_text: {} };
              }
            });
          }

          // Ensure at least one property exists (Notion requirement)
          if (Object.keys(notionProperties).length === 0) {
            notionProperties["Name"] = { title: {} };
          }

          await notion.databases.create({
            parent: {
              type: "page_id",
              page_id: parentPage.id
            },
            title: [
              {
                type: "text",
                text: {
                  content: db.title || db.name || "Database"
                }
              }
            ],
            properties: notionProperties
          });

          // Small delay to prevent rate limiting
          await new Promise(resolve => setTimeout(resolve, 500));
        } catch (dbError) {
          console.error(`Error creating database "${db.title || db.name}":`, dbError);
          // Continue with other databases even if one fails
        }
      }
    }

    // Get Notion user information
    const notionUser = userInfo || await getNotionUserInfo(notion);
    console.log(`Workspace deployed to Notion account: ${notionUser.email} (${notionUser.name})`);

    return { url: parentPage.url, notionUser };
  } catch (error) {
    console.error("Error deploying to Notion:", error);
    throw new Error(`Failed to deploy workspace to Notion: ${error instanceof Error ? error.message : String(error)}`);
  }
}

// Helper function to get user's default Notion page ID
async function getUserNotionPageId(notion: any): Promise<string> {
  try {
    const response = await notion.search({
      filter: {
        value: "page",
        property: "object"
      },
      sort: {
        direction: "descending",
        timestamp: "last_edited_time"
      },
      page_size: 1
    });

    if (response.results && response.results.length > 0) {
      return response.results[0].id;
    }

    throw new Error("No accessible pages found in Notion workspace");
  } catch (error) {
    throw new Error("Could not find a suitable parent page in Notion");
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Workspace routes
  app.get('/api/workspaces', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const workspaces = await storage.getUserWorkspaces(userId);
      res.json(workspaces);
    } catch (error) {
      console.error("Error fetching workspaces:", error);
      res.status(500).json({ message: "Failed to fetch workspaces" });
    }
  });

  app.post('/api/workspaces', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Check usage limits
      if ((user.monthlyUsage || 0) >= (user.usageLimit || 3)) {
        return res.status(403).json({ 
          message: "Monthly usage limit reached. Please upgrade your plan.",
          usageLimit: user.usageLimit || 3,
          currentUsage: user.monthlyUsage || 0
        });
      }

      const validatedData = insertWorkspaceSchema.parse({
        ...req.body,
        userId,
        status: "generating"
      });

      const workspace = await storage.createWorkspace(validatedData);

      // Generate AI workspace specification
      try {
        const aiResponse = await generateNotionWorkspace(
          validatedData.prompt,
          validatedData.theme || "professional"
        );

        const updatedWorkspace = await storage.updateWorkspace(workspace.id, {
          aiResponse,
          status: "completed"
        });

        // Increment user usage
        await storage.updateUserUsage(userId, 1);

        res.json(updatedWorkspace);
      } catch (aiError) {
        console.error("AI generation error:", aiError);
        await storage.updateWorkspace(workspace.id, {
          status: "failed"
        });
        res.status(500).json({ message: "Failed to generate workspace specification" });
      }
    } catch (error) {
      console.error("Error creating workspace:", error);
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid input", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create workspace" });
      }
    }
  });

  app.get('/api/workspaces/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const workspace = await storage.getWorkspace(req.params.id);

      if (!workspace) {
        return res.status(404).json({ message: "Workspace not found" });
      }

      if (workspace.userId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      res.json(workspace);
    } catch (error) {
      console.error("Error fetching workspace:", error);
      res.status(500).json({ message: "Failed to fetch workspace" });
    }
  });

  app.post('/api/workspaces/:id/deploy', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const workspace = await storage.getWorkspace(req.params.id);

      if (!workspace) {
        return res.status(404).json({ message: "Workspace not found" });
      }

      if (workspace.userId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      if (!workspace.aiResponse) {
        return res.status(400).json({ message: "Workspace specification not ready" });
      }

      const notion = await getUncachableNotionClient();

      // Parse the AI-generated workspace data and deploy to Notion
      let workspaceData;
      try {
        workspaceData = typeof workspace.aiResponse === 'string' 
          ? JSON.parse(workspace.aiResponse)
          : workspace.aiResponse;
      } catch (error) {
        console.error("Error parsing workspace data:", error);
        return res.status(400).json({ message: "Invalid workspace data format" });
      }

      // Get Notion user info first for logging and response
      const notionUser = await getNotionUserInfo(notion);
      console.log(`Deploying workspace "${workspaceData.title || 'Untitled'}" to Notion account: ${notionUser.email} (${notionUser.name})`);

      const deployResult = await deployWorkspaceToNotion(notion, workspaceData, notionUser);

      const updatedWorkspace = await storage.updateWorkspace(workspace.id, {
        status: "deployed",
        notionPageId: deployResult.url // Store the Notion page URL
      });

      // Include Notion user info in response for frontend display
      res.json({ ...updatedWorkspace, notionUser: deployResult.notionUser });
    } catch (error) {
      console.error("Error deploying workspace:", error);
      res.status(500).json({ message: "Failed to deploy workspace to Notion" });
    }
  });

  app.post('/api/workspaces/:id/refine', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { refinementPrompt } = req.body;
      const workspace = await storage.getWorkspace(req.params.id);

      if (!workspace) {
        return res.status(404).json({ message: "Workspace not found" });
      }

      if (workspace.userId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      if (!workspace.aiResponse) {
        return res.status(400).json({ message: "No workspace specification to refine" });
      }

      const refinedSpec = await refineWorkspaceSpec(
        workspace.aiResponse as any,
        refinementPrompt
      );

      const updatedWorkspace = await storage.updateWorkspace(workspace.id, {
        aiResponse: refinedSpec,
        status: "completed"
      });

      res.json(updatedWorkspace);
    } catch (error) {
      console.error("Error refining workspace:", error);
      res.status(500).json({ message: "Failed to refine workspace" });
    }
  });

  // Initialize templates if none exist
  const initializeTemplates = async () => {
    try {
      const existingTemplates = await storage.getTemplates();
      if (existingTemplates.length === 0) {
        const defaultTemplates = [
          {
            title: "SaaS CRM System",
            description: "Complete customer relationship management with lead tracking, deal pipeline, and revenue analytics.",
            category: "business",
            prompt: "Create a comprehensive SaaS CRM system with lead tracking, deal pipeline management, revenue analytics, churn rate calculations, and customer communication logs.",
            tags: ["Leads Database", "Deal Pipeline", "Analytics"],
            preview: {
              databases: 3,
              properties: 25,
              views: 8
            },
            isPublic: true,
            usageCount: 0
          },
          {
            title: "Project Management Hub",
            description: "Task boards, timelines, resource allocation, and team coordination in one workspace.",
            category: "productivity",
            prompt: "Build a project management workspace with task boards, timeline views, resource allocation, team coordination, milestone tracking, and progress reporting.",
            tags: ["Kanban Boards", "Timeline", "Resources"],
            preview: {
              databases: 4,
              properties: 20,
              views: 6
            },
            isPublic: true,
            usageCount: 0
          },
          {
            title: "Content Calendar",
            description: "Multi-platform content planning with collaboration tools and performance analytics.",
            category: "marketing",
            prompt: "Create a content calendar workspace for multi-platform content planning, collaboration tools, publishing schedules, performance analytics, and content ideation.",
            tags: ["Multi-Platform", "Collaboration", "Analytics"],
            preview: {
              databases: 3,
              properties: 18,
              views: 5
            },
            isPublic: true,
            usageCount: 0
          },
          {
            title: "Life Organization System",
            description: "Habit tracking, goal management, and personal productivity system for life organization.",
            category: "personal",
            prompt: "Design a personal life organization system with habit tracking, goal management, daily journaling, mood tracking, and personal productivity metrics.",
            tags: ["Habit Tracker", "Goals", "Journal"],
            preview: {
              databases: 4,
              properties: 22,
              views: 7
            },
            isPublic: true,
            usageCount: 0
          },
          {
            title: "E-commerce Operations",
            description: "Product catalog, order management, inventory tracking, and customer support system.",
            category: "business",
            prompt: "Build an e-commerce operations workspace with product catalog, order management, inventory tracking, customer support tickets, and sales analytics.",
            tags: ["Products", "Orders", "Inventory"],
            preview: {
              databases: 5,
              properties: 30,
              views: 10
            },
            isPublic: true,
            usageCount: 0
          }
        ];

        for (const template of defaultTemplates) {
          await storage.createTemplate(template);
        }
        console.log('Initialized default templates');
      }
    } catch (error) {
      console.error('Error initializing templates:', error);
    }
  };

  // Initialize templates on startup
  await initializeTemplates();

  // Template routes
  app.get('/api/templates', async (req, res) => {
    try {
      const { category } = req.query;
      const templates = category 
        ? await storage.getTemplatesByCategory(category as string)
        : await storage.getTemplates();
      res.json(templates);
    } catch (error) {
      console.error("Error fetching templates:", error);
      res.status(500).json({ message: "Failed to fetch templates" });
    }
  });

  app.get('/api/templates/:id', async (req, res) => {
    try {
      const template = await storage.getTemplate(req.params.id);
      if (!template) {
        return res.status(404).json({ message: "Template not found" });
      }
      res.json(template);
    } catch (error) {
      console.error("Error fetching template:", error);
      res.status(500).json({ message: "Failed to fetch template" });
    }
  });

  app.post('/api/templates/:id/use', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const templateId = req.params.id;

      // Handle both database templates and featured templates
      let template;
      let prompt;
      let title;
      let description;

      if (templateId.startsWith('featured-')) {
        // Handle featured templates with hardcoded data
        const featuredTemplates = {
          'featured-crm': {
            title: 'SaaS CRM System',
            description: 'Complete customer relationship management with lead tracking, deal pipeline, and revenue analytics.',
            prompt: 'Create a comprehensive SaaS CRM system with lead tracking, deal pipeline management, revenue analytics, churn rate calculations, and customer communication logs.'
          },
          'featured-project': {
            title: 'Project Management Hub', 
            description: 'Task boards, timelines, resource allocation, and team coordination in one workspace.',
            prompt: 'Build a project management workspace with task boards, timeline views, resource allocation, team coordination, milestone tracking, and progress reporting.'
          },
          'featured-content': {
            title: 'Content Calendar',
            description: 'Multi-platform content planning with collaboration tools and performance analytics.',
            prompt: 'Create a content calendar workspace for multi-platform content planning, collaboration tools, publishing schedules, performance analytics, and content ideation.'
          },
          'featured-personal': {
            title: 'Life Organization System',
            description: 'Habit tracking, goal management, and personal productivity system for life organization.',
            prompt: 'Design a personal life organization system with habit tracking, goal management, daily journaling, mood tracking, and personal productivity metrics.'
          },
          'featured-ecommerce': {
            title: 'E-commerce Operations',
            description: 'Product catalog, order management, inventory tracking, and customer support system.',
            prompt: 'Build an e-commerce operations workspace with product catalog, order management, inventory tracking, customer support tickets, and sales analytics.'
          }
        };

        const featuredTemplate = featuredTemplates[templateId as keyof typeof featuredTemplates];
        if (!featuredTemplate) {
          return res.status(404).json({ message: "Featured template not found" });
        }

        title = featuredTemplate.title;
        description = featuredTemplate.description;
        prompt = featuredTemplate.prompt;
      } else {
        // Handle database templates
        template = await storage.getTemplate(templateId);
        if (!template) {
          return res.status(404).json({ message: "Template not found" });
        }
        title = template.title;
        description = template.description;
        prompt = template.prompt;
      }

      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Check usage limits
      if ((user.monthlyUsage || 0) >= (user.usageLimit || 3)) {
        return res.status(403).json({ 
          message: "Monthly usage limit reached. Please upgrade your plan.",
          usageLimit: user.usageLimit || 3,
          currentUsage: user.monthlyUsage || 0
        });
      }

      // Create workspace from template
      const workspace = await storage.createWorkspace({
        userId,
        title,
        description,
        prompt,
        theme: req.body.theme || "professional",
        templateId: templateId.startsWith('featured-') ? undefined : templateId,
        status: "generating"
      });

      // Generate AI response
      try {
        const aiResponse = await generateNotionWorkspace(
          prompt,
          req.body.theme || "professional"
        );

        const updatedWorkspace = await storage.updateWorkspace(workspace.id, {
          aiResponse,
          status: "completed"
        });

        // Increment template usage only for database templates
        if (template) {
          await storage.incrementTemplateUsage(template.id);
        }

        await storage.updateUserUsage(userId, 1);

        res.json(updatedWorkspace);
      } catch (aiError) {
        console.error("AI generation error:", aiError);
        await storage.updateWorkspace(workspace.id, {
          status: "failed"
        });
        res.status(500).json({ message: "Failed to generate workspace from template" });
      }
    } catch (error) {
      console.error("Error using template:", error);
      res.status(500).json({ message: "Failed to use template" });
    }
  });

  // Chat routes
  app.post('/api/chat', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { messages, workspaceId } = req.body;

      const chatResponse = await generateChatResponse(messages);

      // Save conversation if workspaceId provided
      if (workspaceId) {
        const updatedMessages = [...messages, {
          role: "assistant",
          content: chatResponse,
          timestamp: new Date().toISOString()
        }];

        // Try to update existing conversation or create new one
        try {
          const existingConversations = await storage.getUserConversations(userId);
          const workspaceConversation = existingConversations.find(c => c.workspaceId === workspaceId);

          if (workspaceConversation) {
            await storage.updateConversation(workspaceConversation.id, updatedMessages);
          } else {
            await storage.createConversation({
              userId,
              workspaceId,
              messages: updatedMessages
            });
          }
        } catch (convError) {
          console.error("Error saving conversation:", convError);
          // Continue with response even if conversation save fails
        }
      }

      res.json({ response: chatResponse });
    } catch (error) {
      console.error("Error in chat:", error);
      res.status(500).json({ message: "Failed to process chat message" });
    }
  });

  // Stripe subscription routes (only if Stripe is configured)
  if (stripe) {
    app.post('/api/create-subscription', isAuthenticated, async (req: any, res) => {
      try {
        const userId = req.user.claims.sub;
        let user = await storage.getUser(userId);

        if (!user) {
          return res.status(404).json({ message: "User not found" });
        }

        if (user.stripeSubscriptionId) {
          const subscription = await stripe.subscriptions.retrieve(user.stripeSubscriptionId);
          return res.send({
            subscriptionId: subscription.id,
            clientSecret: (subscription.latest_invoice as any)?.payment_intent?.client_secret,
          });
        }

        if (!user.email) {
          return res.status(400).json({ message: 'No user email on file' });
        }

        const customer = await stripe.customers.create({
          email: user.email,
          name: user.firstName && user.lastName ? `${user.firstName} ${user.lastName}` : user.email,
        });

        const subscription = await stripe.subscriptions.create({
          customer: customer.id,
          items: [{
            price: process.env.STRIPE_PRICE_ID || 'price_default', // User needs to set this
          }],
          payment_behavior: 'default_incomplete',
          expand: ['latest_invoice.payment_intent'],
        });

        await storage.updateUserStripeInfo(userId, customer.id, subscription.id);

        res.send({
          subscriptionId: subscription.id,
          clientSecret: (subscription.latest_invoice as any)?.payment_intent?.client_secret,
        });
      } catch (error: any) {
        console.error("Stripe subscription error:", error);
        return res.status(400).send({ error: { message: error.message } });
      }
    });
  } else {
    // Placeholder route if Stripe not configured
    app.post('/api/create-subscription', (req, res) => {
      res.status(503).json({ message: "Payment processing not configured. Please set STRIPE_SECRET_KEY." });
    });
  }

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  workspaces,
  templates,
  conversations,
  type User,
  type UpsertUser,
  type Workspace,
  type InsertWorkspace,
  type Template,
  type InsertTemplate,
  type Conversation,
  type InsertConversation,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, count, sql } from "drizzle-orm";

// Interface for storage operations
export interface IStorage {
  // User operations
  // (IMPORTANT) these user operations are mandatory for Replit Auth.
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserStripeInfo(userId: string, stripeCustomerId: string, stripeSubscriptionId: string): Promise<User>;
  updateUserUsage(userId: string, increment: number): Promise<User>;
  resetMonthlyUsage(userId: string): Promise<User>;

  // Workspace operations
  createWorkspace(workspace: InsertWorkspace): Promise<Workspace>;
  getWorkspace(id: string): Promise<Workspace | undefined>;
  getUserWorkspaces(userId: string): Promise<Workspace[]>;
  updateWorkspace(id: string, updates: Partial<Workspace>): Promise<Workspace>;
  deleteWorkspace(id: string): Promise<void>;

  // Template operations
  getTemplates(): Promise<Template[]>;
  getTemplate(id: string): Promise<Template | undefined>;
  getTemplatesByCategory(category: string): Promise<Template[]>;
  createTemplate(template: InsertTemplate): Promise<Template>;
  incrementTemplateUsage(id: string): Promise<void>;

  // Conversation operations
  createConversation(conversation: InsertConversation): Promise<Conversation>;
  getConversation(id: string): Promise<Conversation | undefined>;
  getUserConversations(userId: string): Promise<Conversation[]>;
  updateConversation(id: string, messages: any[]): Promise<Conversation>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  // (IMPORTANT) these user operations are mandatory for Replit Auth.

  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUserStripeInfo(userId: string, stripeCustomerId: string, stripeSubscriptionId: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({
        stripeCustomerId,
        stripeSubscriptionId,
        subscriptionStatus: "active",
        usageLimit: 80,
        updatedAt: new Date(),
      })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  async updateUserUsage(userId: string, increment: number): Promise<User> {
    const [user] = await db
      .update(users)
      .set({
        monthlyUsage: sql`${users.monthlyUsage} + ${increment}`,
        updatedAt: new Date(),
      })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  async resetMonthlyUsage(userId: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({
        monthlyUsage: 0,
        updatedAt: new Date(),
      })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  // Workspace operations
  async createWorkspace(workspace: InsertWorkspace): Promise<Workspace> {
    const [newWorkspace] = await db
      .insert(workspaces)
      .values(workspace)
      .returning();
    return newWorkspace;
  }

  async getWorkspace(id: string): Promise<Workspace | undefined> {
    const [workspace] = await db
      .select()
      .from(workspaces)
      .where(eq(workspaces.id, id));
    return workspace;
  }

  async getUserWorkspaces(userId: string): Promise<Workspace[]> {
    return await db
      .select()
      .from(workspaces)
      .where(eq(workspaces.userId, userId))
      .orderBy(desc(workspaces.createdAt));
  }

  async updateWorkspace(id: string, updates: Partial<Workspace>): Promise<Workspace> {
    const [workspace] = await db
      .update(workspaces)
      .set({
        ...updates,
        updatedAt: new Date(),
      })
      .where(eq(workspaces.id, id))
      .returning();
    return workspace;
  }

  async deleteWorkspace(id: string): Promise<void> {
    await db.delete(workspaces).where(eq(workspaces.id, id));
  }

  // Template operations
  async getTemplates(): Promise<Template[]> {
    return await db
      .select()
      .from(templates)
      .where(eq(templates.isPublic, true))
      .orderBy(desc(templates.usageCount));
  }

  async getTemplate(id: string): Promise<Template | undefined> {
    const [template] = await db
      .select()
      .from(templates)
      .where(eq(templates.id, id));
    return template;
  }

  async getTemplatesByCategory(category: string): Promise<Template[]> {
    return await db
      .select()
      .from(templates)
      .where(and(eq(templates.category, category), eq(templates.isPublic, true)))
      .orderBy(desc(templates.usageCount));
  }

  async createTemplate(template: InsertTemplate): Promise<Template> {
    const [newTemplate] = await db
      .insert(templates)
      .values(template)
      .returning();
    return newTemplate;
  }

  async incrementTemplateUsage(id: string): Promise<void> {
    await db
      .update(templates)
      .set({
        usageCount: sql`${templates.usageCount} + 1`,
      })
      .where(eq(templates.id, id));
  }

  // Conversation operations
  async createConversation(conversation: InsertConversation): Promise<Conversation> {
    const [newConversation] = await db
      .insert(conversations)
      .values(conversation)
      .returning();
    return newConversation;
  }

  async getConversation(id: string): Promise<Conversation | undefined> {
    const [conversation] = await db
      .select()
      .from(conversations)
      .where(eq(conversations.id, id));
    return conversation;
  }

  async getUserConversations(userId: string): Promise<Conversation[]> {
    return await db
      .select()
      .from(conversations)
      .where(eq(conversations.userId, userId))
      .orderBy(desc(conversations.updatedAt));
  }

  async updateConversation(id: string, messages: any[]): Promise<Conversation> {
    const [conversation] = await db
      .update(conversations)
      .set({
        messages,
        updatedAt: new Date(),
      })
      .where(eq(conversations.id, id))
      .returning();
    return conversation;
  }
}

export const storage = new DatabaseStorage();

